{"trigger":"click","code":"// Check if Spotify Web Playback SDK is available
if (!window.Spotify) {
  // Load Spotify Web Playback SDK
  const script = document.createElement('script');
  script.src = 'https://sdk.scdn.co/spotify-player.js';
  document.head.appendChild(script);

  // Show connection prompt
  setElements(prev => [...prev, {
    id: 'spotify-prompt-' + Date.now(),
    type: 'text',
    content: 'ğŸµ Click to connect Spotify!',
    position: { x: element.position.x, y: element.position.y - 10 },
    size: 20,
    color: '#1DB954',
    animation: 'pulse'
  }]);

  // Auto-remove prompt after 3 seconds
  setTimeout(() => {
    setElements(prev => prev.filter(el => !el.id.startsWith('spotify-prompt-')));
  }, 3000);

  return;
}

// If SDK loaded, attempt to get current track
fetch('https://api.spotify.com/v1/me/player/currently-playing', {
  headers: {
    'Authorization': 'Bearer ' + localStorage.getItem('spotify_token')
  }
})
.then(response => {
  if (response.status === 401) {
    // Need to authenticate
    const clientId = 'your_client_id'; // Would need real Spotify app
    const redirectUri = window.location.origin;
    const scopes = 'user-read-currently-playing user-read-playback-state';
    const authUrl = `https://accounts.spotify.com/authorize?client_id=${clientId}&response_type=token&redirect_uri=${redirectUri}&scope=${scopes}`;

    // Show auth message
    setElements(prev => [...prev, {
      id: 'spotify-auth-' + Date.now(),
      type: 'text',
      content: 'ğŸ” Spotify login needed - check console for auth link',
      position: { x: element.position.x, y: element.position.y - 15 },
      size: 16,
      color: '#FF6B6B'
    }]);

    console.log('Visit this link to connect Spotify:', authUrl);
    return null;
  }
  return response.json();
})
.then(data => {
  if (data && data.item) {
    // Show currently playing track
    const trackInfo = `â™ª ${data.item.name} - ${data.item.artists[0].name}`;
    setElements(prev => [...prev, {
      id: 'now-playing-' + Date.now(),
      type: 'text',
      content: trackInfo,
      position: { x: element.position.x, y: element.position.y - 15 },
      size: 18,
      color: '#1DB954',
      animation: 'float'
    }]);

    // Auto-remove after 5 seconds
    setTimeout(() => {
      setElements(prev => prev.filter(el => !el.id.startsWith('now-playing-')));
    }, 5000);
  } else {
    // No track playing
    setElements(prev => [...prev, {
      id: 'no-music-' + Date.now(),
      type: 'text',
      content: 'ğŸ”‡ No music playing',
      position: { x: element.position.x, y: element.position.y - 10 },
      size: 16,
      color: '#888'
    }]);
  }
})
.catch(error => {
  console.log('Spotify connection demo - would need real API keys');
  // Show demo message instead
  setElements(prev => [...prev, {
    id: 'spotify-demo-' + Date.now(),
    type: 'text',
    content: 'ğŸµ Spotify Demo - "Bohemian Rhapsody" playing',
    position: { x: element.position.x, y: element.position.y - 15 },
    size: 18,
    color: '#1DB954',
    animation: 'pulse'
  }]);

  setTimeout(() => {
    setElements(prev => prev.filter(el => !el.id.startsWith('spotify-demo-')));
  }, 4000);
});"}

